import React, { useState } from 'react';
import axios from 'axios';

const SEARCH_ENDPOINT = 'https://api.spotify.com/v1/search/';

function Search({ token, setData }) {
  const [searchInput, setSearchInput] = useState('');

  const performSearch = async (searchInput) => {
    const items = await searchQuery(token, 'track', searchInput);
    console.log(items);
    setData(items);
  };

  const searchQuery = async (token, type = 'artist', query) => {
    const { data } = await axios.get(SEARCH_ENDPOINT, {
      headers: {
        Authorization: `Bearer ${token}`,
      },
      params: {
        q: query,
        type: type,
      },
    });

    const items = Object.values(data)[0].items;

    return items;
  };

  return (
    <div>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          performSearch(searchInput);
        }}
      >
        <input type="text" onChange={(e) => setSearchInput(e.target.value)} />
        <button type={'submit'}>Search</button>
      </form>
    </div>
  );
}

export default Search;
