import React, { useState } from 'react';

function Track({
  track: {
    name,
    artists,
    album: { images },
  },
  hideButton,
  addTrack,
}) {
  const [isAdd, setIsAdd] = useState(false);

  return (
    <div className="track">
      <img src={images[0].url} alt="Track" />
      <div className="info">
        <h3>{name}</h3>
        <div>
          <ul>
            {artists.map((artist) => (
              <li key={artist.id}>{artist.name}</li>
            ))}
          </ul>
        </div>
        {hideButton && (
          <button
            type="button"
            onClick={(e) => {
              this.addTrack();
              setIsAdd(!isAdd);
            }}
          ></button>
        )}
      </div>
    </div>
  );
}

export default Track;
